import React, { memo } from 'react';
import Background from './Background';
import Logo from './Logo';
import Header from './Header';
import Paragraph from './Paragraph';
import Button from './Button';

import {
  View,
  Text,
  StyleSheet,
  FlatList,
} from 'react-native';

const Question = (props) => {
  return (
    <View style={styles.questionDataContainer}>

    <View style={styles.questionData}>

      <Text style={styles.questionDescription}>{props.question}</Text>

    </View>

    <FlatList
        style={styles.questionOptions}
        data={props.options}
        contentContainerStyle={styles.questionOptionsContainer}
        renderItem={({ item }) => (
          <Button mode="outlined" onPress={() => props.onItemSelected(item)}>
            {item}
          </Button>
        )}
        keyExtractor={(item, index) => `${index}-${item}`}
        onPressItem={props.onItemSelected}
        scrollEnabled={true}
      />
  </View>
  )
};

const styles = StyleSheet.create({
  questionDataContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginLeft: scale(16),
    marginRight: scale(16),
  },

  questionData: {
    padding: scale(16),
    marginTop: scale(32),
    marginBottom: scale(32),
    alignSelf: 'stretch',
    maxHeight: verticalScale(280),
    borderWidth: 2,
    borderRadius: 8,
    borderColor: '#ffffff',
    justifyContent: 'center',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
  },

  questionDescription: {
    color: '#000',
    fontSize: moderateScale(20),
    textAlign: 'center',
  },

  questionOptions: {
    width: '100%',
  },
  questionOptionsContainer: {
    marginTop: 0,
  }
});

export default memo(Question);
