import * as React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { Appbar, BottomNavigation } from 'react-native-paper';
import Constants from 'expo-constants';

import { createAppContainer, NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';

import {
  Home,
  Login,
  Register,
  ForgotPassword,
  Dashboard,
} from './views';

const siteName = 'workingTitle';

const AccRoute = () => <Text>Account</Text>;

const ChatRoute = () => <Text>Chat</Text>;

const DashRoute = () => <Dashboard />;

const PlayRoute = () => <Text>Play</Text>;

const RedeemRoute = () => <Text>Redeem</Text>;

export default function App() {
  const [index, setIndex] = React.useState(2);
  const [routes] = React.useState([

    { key: 'account', title: 'Account', focusedIcon: 'account' , unfocusedIcon: 'account-outline' },
    { key: 'chat', title: 'Chat', focusedIcon: 'chat', unfocusedIcon: 'chat-outline' },
    { key: 'dashboard', title: 'Dashboard', focusedIcon: 'view-dashboard', unfocusedIcon: 'view-dashboard-outline'},
    { key: 'play', title: 'Play', focusedIcon: 'gamepad-variant', unfocusedIcon: 'gamepad-variant-outline' },
    { key: 'redeem', title: 'Redeem', focusedIcon: 'gift', unfocusedIcon: 'gift-outline'},
  ]);

  const renderScene = BottomNavigation.SceneMap({
    account: AccRoute,
    chat: ChatRoute,
    dashboard: DashRoute,
    play: PlayRoute,
    redeem: RedeemRoute,
  });
  const Stack = createStackNavigator(); 
  const Tab = createMaterialBottomTabNavigator();

  return (
    <p>placeholder</p>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    paddingTop: Constants.statusBarHeight,
    backgroundColor: '#ecf0f1',
    padding: 8,
  },
  paragraph: {
    margin: 24,
    fontSize: 18,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});
